$(document).ready(function () {

    var areaValue = $("#range-area"),
            investValue = $("#range-investments"),
            roomType_0 = $("#room-type-0"),
            roomType_1 = $("#room-type-1"),
            totalAmount = $(".total-box__amount");

    areaValue.ionRangeSlider({
        min: 40,
        max: 80,
        step: 5,
        hide_min_max: true,
        postfix: " м<sup>2</sup>",
        onChange: getAmount
    });

    $(".range-slider").animate({"opacity": 1}, 200);

    roomType_0.change(getAmount);
    roomType_1.change(getAmount);
    getAmount();

    function getAmount() {
        var roomType = (roomType_0.prop("checked")) ? 0 : 1;
        if (roomType === 0) {
            switch (+areaValue.val()) {
                case 40:
                    investValue.html("750 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("1 800 000");
                    break;
                case 45:
                    investValue.html("800 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("2 200 000");
                    break;
                case 50:
                    investValue.html("850 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("2 400 000");
                    break;
                case 55:
                    investValue.html("900 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("2 800 000");
                    break;
                case 60:
                    investValue.html("1 000 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("3 100 000");
                    break;
                case 65:
                    investValue.html("1 100 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("3 200 000");
                    break;
                case 70:
                    investValue.html("1 200 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("3 300 000");
                    break;
                case 75:
                    investValue.html("1 300 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("3 400 000");
                    break;
                case 80:
                    investValue.html("1 400 000 <span class='sym-ruble'>₽</span>");
                    totalAmount.text("3 500 000");
                    break;
            }
        } else {
            switch (+areaValue.val()) {
                case 40:
                    investValue.text("750 000 ₽");
                    totalAmount.text("1 300 000");
                    break;
                case 45:
                    investValue.text("800 000 ₽");
                    totalAmount.text("1 500 000");
                    break;
                case 50:
                    investValue.text("850 000 ₽");
                    totalAmount.text("1 800 000");
                    break;
                case 55:
                    investValue.text("900 000 ₽");
                    totalAmount.text("2 000 000");
                    break;
                case 60:
                    investValue.text("1 000 000 ₽");
                    totalAmount.text("2 400 000");
                    break;
                case 65:
                    investValue.text("1 100 000 ₽");
                    totalAmount.text("2 500 000");
                    break;
                case 70:
                    investValue.text("1 200 000 ₽");
                    totalAmount.text("2 600 000");
                    break;
                case 75:
                    investValue.text("1 300 000 ₽");
                    totalAmount.text("2 700 000");
                    break;
                case 80:
                    investValue.text("1 400 000 ₽");
                    totalAmount.text("2 800 000");
                    break;
            }
        }
    }

    $(document).on('click', '.calculator__submit', function () {
        /*var parent_1 = $('#calculator_form input[name="room-type"]').val(),
         parent_2 = $('#calculator_form input[name="range-area"]').val(),
         parent_3 = $('#calculator_form input[name="range-investments"]').val();
         $('#form_message input[name="room-type"]').val(parent_1);
         $('#form_message input[name="range-area"]').val(parent_2);
         $('#form_message input[name="range-investments"]').val(parent_3);*/
        destination = $("footer").offset().top - 110;
        $("body, html").animate({scrollTop: destination}, 1000);
    });

});
